import React, { Component } from 'react';
import './index.css';

export class NavButton extends Component {
state = {
    bgColor: "black",
    lineH: ".18em",
    lineW: "30px",
    isClicked: false,
    display: "block",
    centerBarmargin: ".34em",
}

handleClick = (e) => {
  e.preventDefault();
  !this.state.isClicked ? this.setState({
    display: "none",
    isClicked: true,
  })
  :
  this.setState({
    display: "block",
    isClicked: false
  })
  console.log(this.state.isClicked)
}

  render() {
    return (
      <div className="button-border" onClick={this.handleClick}>
         <div  className="button-line-top"style={{ backgroundColor: this.state.bgColor, height: this.state.lineH, width: this.state.lineW, display: this.state.display }}></div>
         <div  className="button-line-center"style={{ backgroundColor: this.state.bgColor, marginTop: this.state.centerBarmargin }}></div>
         <div  className="button-line-bottum"style={{ backgroundColor: this.state.bgColor, height: this.state.lineH, width: this.state.lineW, display: this.state.display }}></div>
      </div>
    )
  }
}

export default NavButton;
